const defaultState = { statusSession: false, user: null };

const reducer = (state = defaultState, action) => {
  switch (action.type) {
    case 'LOGIN':
      console.log({
        statusSession: true,
      });
      return {
        ...state,
        statusSession: true,
      };
    default:
      return state;
  }
};

export default reducer;
