const defaultState = { statusSession: false, user: null };

const reducer = (state = defaultState, action) => {
  switch (action.type) {
    case 'LOGIN':
      return {
        ...state,
        statusSession: true,
        user: { name: action.session.name },
      };
    default:
      return state;
  }
};

export default reducer;
