import { call, put, takeEvery } from 'redux-saga/effects';
import { loadingStart, loadingTodo } from '../actioncreators/actionsSaga';

const fetchTodo = async () => {
  return await (await fetch('/', { method: 'POST' })).json();
};

function* loadTodo() {
  try {
    yield;
    put(loadingStart());
    const result = yield call(fetchTodo);
    yield put(loadingTodo(result.todo));
  } catch (error) {
    console.log(error);
  }
}

// Функция-наблюдатель.
function* saga() {
  yield takeEvery({ type: 'loadTodo' }, loadTodo);
}

export default saga;
